import React from 'react';
import { useSelector, useDispatch } from 'react-redux';




const Transactions = () => {


	const T = useSelector( (state) => state.transactions.transactions);
	console.log(T);


	return (
		<div className="transactions">
			<span className="transactions--title">transaction history</span>
			<div className="transactions--wrapper">
				{T.length == 0 
					? (<span className="transactions--wrapper--message">no history to show yet</span>)
					: (
						// <span class="transactions--wrapper--message">some history to show</span>
						T.map((t) => (
							<div key={t.id} className="transactions--item">
								<i style={{backgroundColor:colours[i].colour}}></i>
								<span className="transactions--item--text" name="date">transaction date: {t.transactionDate}</span>
								<span className="transactions--item--text" name="type">transaction type: {t.transactionType}</span>
								<span className="transactions--item--text" name="amount">transaction amount: {t.transactionAmount}</span>
							</div>
						))
				)}
			</div>
		</div>
	)
}

export default Transactions;
